import { Injectable } from '@angular/core';
import { HttpClient, HttpResponse, HttpErrorResponse } from '@angular/common/http'
import { Entry } from './entry';
import { map, catchError } from 'rxjs/operators';
import { Observable, of, throwError } from 'rxjs';


@Injectable({
  providedIn: 'root'
})

export class EntryService {

  private baseURL = "http://localhost:8081/app/api/v1";

  constructor(private httpClient: HttpClient) { }

  saveEntry(entry: Entry) : Observable<any> {
    return this.httpClient.post(`${this.baseURL}/entry`, entry).pipe(catchError(this.handleError));
  }

  getTop10EntryList(): Observable<Entry[]>{
      return this.httpClient.get<Entry[]>(`${this.baseURL}/entries/top-scores`);
  }

  private handleError(error: HttpErrorResponse): Observable<HttpErrorResponse> {
    console.log('An error occurred: ', error);
    return throwError(error);
  }

}
