import { Component, OnInit } from '@angular/core';
import { Entry } from '../entry'
import { EntryService } from '../entry.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-list-entry',
  templateUrl: './list-entry.component.html',
  styleUrls: ['./list-entry.component.css']
})
export class ListEntryComponent implements OnInit {
   entries: Entry[] = [];

    constructor(private entryService: EntryService, private router: Router) { }

    ngOnInit(): void {
      this.getTop10Entries();
    }

    private getTop10Entries(){
       this.entryService.getTop10EntryList().subscribe(data => {
            this.entries = data;
          });
    }

    backToSave() {
      this.router.navigate(['/save-entry']);
    }

}
<div class = "row">
  <h2> Top 10 Scores</h2>
</div>

<table class = "table table-striped table-bordered">
  <thead>
  <tr>
    <th> Word </th>
    <th> Score </th>
  </tr>
  </thead>
  <tbody>
  <tr *ngFor = "let entry of entries" >
    <td> {{ entry.word }} </td>
    <td> {{ entry.score }} </td>
  </tr>
  </tbody>
</table>

<br />
<div class="btn-toolbar justify-content-center">
  <button type="button" id="btnSave" class="btn btn-primary btn-sm mr-3" (click)="backToSave()">Back To Save Entry</button>
</div>
